import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AuthenticationService {

  private apiUrl = 'http://localhost:3000'; // Postavite URL servera

  constructor(private http: HttpClient) {}

  loginStudent(username: string, password: string) {
    const body = { username, password };

    return this.http.post(`${this.apiUrl}/auth/login`, body);
  }

  loginProfesor(username: string, password: string) {
    const body = { username, password };

    return this.http.post(`${this.apiUrl}/auth-profesor/login`, body);
  }
}
