import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthenticationService } from 'src/app/auth/authentication.service';

@Component({
  selector: 'app-profesor-strana',
  templateUrl: './profesor-strana.component.html',
  styleUrls: ['./profesor-strana.component.css']
})
export class ProfesorStranaComponent implements OnInit{

  currentUser:any=null;

  constructor(private authService:AuthenticationService, private router: Router){

  }

  ngOnInit(): void {
    this.authService.fetchCurrentUser(this.currentUser);
  }



  logout() {
    localStorage.removeItem(this.currentUser.email);
  }
}

<mat-sidenav-container class="sidenav-container">
    <mat-sidenav #sidenav class="sidenav">
        <!-- Ime i prezime trenutnog korisnika -->
        <div class="user-info">
            <mat-icon>school</mat-icon> <!-- Ikonica za profesora -->
            <span>Profesor</span> <!-- Tekst za profesora -->
        </div>
        <div class="user-info">
            <mat-icon>person</mat-icon>
            <!-- <span>{{ currentUser.ime }} {{ currentUser.prezime }}</span> -->
        </div>
        <!-- Ikona za logout -->
        <button mat-icon-button (click)="logout()">
            <mat-icon>exit_to_app</mat-icon>
        </button>
    </mat-sidenav>
    <mat-sidenav-content>
    </mat-sidenav-content>
</mat-sidenav-container>
