import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { environment } from '../environments/environment';

@Injectable({
  providedIn: 'root'
})
export class AuthenticationService {

  constructor(private http: HttpClient) {}

  currentUserStudent:any;
  currentUserProfesor:any;

  loginStudent(email: string, password: string) {
    const body = { email, password };
    this.currentUserStudent=this.http.post(environment.api+`/auth/login`, body);
    return this.currentUserStudent;
  }

  loginProfesor(email: string, password: string) {
    const body = { email, password };

    this.currentUserProfesor=this.http.post(environment.api+`/auth-profesor/login`, body);
    return this.currentUserProfesor;
  }

  fetchCurrentProfesor() {
    return this.currentUserProfesor;
  }
}
